package com.conmio.mediahackday.headlineimport com.conmio.mediahackday.HeadlineRepositoryimport grails.plugins.rest.client.RestBuilderimport groovyx.net.http.ContentType/** * Created with IntelliJ IDEA. * User: haren * Date: 10/5/13 * Time: 5:51 PM * To change this template use File | Settings | File Templates. */class InMemHeadlineRespository implements HeadlineRepository{    def grailsApplication    def httpService    def rest = new RestBuilder()    List<Headline> getHeadlines() {        List<Headline> headlines = []        String url = grailsApplication.config.afp4w.api.base.url        //Todo Improve Rest Builder        def resp = httpService.request(url,"POST",[type:ContentType.XML])        resp.NewsItem.NewsComponent.NewsComponent.each { item ->          /*  println("hello" + item.dump())*/            headlines.add(new Headline(item))        }        return headlines    }}